<template>
  <nav class="navbar">
    <div class="navbar-container">
      <div class="desktop-menu">
        <router-link to="/" class="nav-link">Home</router-link>
        <router-link to="/about" class="nav-link">About</router-link>
        <router-link to="/staff" class="nav-link">Staff</router-link>
        <router-link to="/join" class="nav-link">Join</router-link>
        <a href="https://webchat.verbabble.com/" class="nav-link">Chat</a>
      </div>
      <div class="mobile-menu">
        <div class="hamburger-menu" @click="toggleMobileMenu">&#9776;</div>
        <div v-if="isMobileMenuOpen" class="mobile-menu-items">
          <router-link to="/" class="nav-link">Home</router-link>
          <router-link to="/about" class="nav-link">About</router-link>
          <router-link to="/staff" class="nav-link">Staff</router-link>
          <router-link to="/join" class="nav-link">Join</router-link>
          <a href="https://webchat.verbabble.com/" class="nav-link">Chat</a>
        </div>
      </div>
    </div>
  </nav>
</template>

<script>
import { defineComponent, ref } from 'vue'

export default defineComponent({
  setup() {
    const isMobileMenuOpen = ref(false)

    const toggleMobileMenu = () => {
      isMobileMenuOpen.value = !isMobileMenuOpen.value
    }

    return {
      isMobileMenuOpen,
      toggleMobileMenu
    }
  }
})
</script>

<style scoped>
.navbar {
  position: fixed;
  top: 0;
  width: 100%;
  background-color: rgba(255, 255, 255, 0.7);
  z-index: 1000;
}

.navbar-container {
  display: flex;
  justify-content: space-between;
}

.desktop-menu {
  display: flex;
}

.mobile-menu {
  display: none;
}

.hamburger-menu {
  display: block;
  cursor: pointer;
  position: absolute;
  right: 0;
  top: 0;
  padding: 10px;
  margin: 10px;
  background-color: rgba(255, 255, 255, 0.7);
  border: none;
}

.hamburger-menu::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: auto;
  background-color: rgba(255, 255, 255, 0.7);
  z-index: 1;
}

.mobile-menu-items {
  display: flex;
  flex-direction: column;
}

.nav-link {
  text-decoration: none;
  color: rgba(59, 0, 64, 255);
  margin: 0 10px;
}

.nav-link:hover {
  text-decoration: underline;
}

@media (max-width: 768px) {
  .desktop-menu {
    display: none;
  }

  .mobile-menu {
    display: block;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background-color: rgba(255, 255, 255, 0.7);
  }

  .navbar {
    background-color: rgba(255, 255, 255, 0.7);
  }
}
</style>
